INNER JOIN designer d ON d.designer_id = i.designer_id
WHERE i.designer_id = #{designer_id}

SELECT i.item_id, i.item_name, d.designer_name, i.item_price, i.item_photo FROM item i
INNER JOIN designer d ON d.designer_id = i.designer_id
ORDER BY i.item_id LIMIT #{limit} OFFSET #{offset};
